import React from 'react';
import {Layout, Menu, Breadcrumb, Form, Input, Cascader, Checkbox, Button, message} from 'antd';
import {
  DesktopOutlined,
  PieChartOutlined,
  FileOutlined,
  TeamOutlined,
  UserOutlined,
} from '@ant-design/icons';
import { Link } from 'react-router-dom';
import '../assets/css/PersonalInfo.css';
import BasicInformation from "../Part1File/BasicInformation";
import axios from "axios";
import UserInfo from "./UserInfo";


let storage = window.localStorage;
const defaultUrl = 'http://127.0.0.1:5003';

const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 8 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 16 },
  },
};
const tailFormItemLayout = {
  wrapperCol: {
    xs: {
      span: 24,
      offset: 0,
    },
    sm: {
      span: 16,
      offset: 8,
    },
  },
};

// let temp = storage['address']
// let userAddress = ['','','']
// for (let i = 0; i < temp.length; i++) {
//     let count = 0
//     if (temp[i] !== ',') {
//         userAddress[count] = userAddress[count] + temp[i]
//     } else {
//         count++;
//     }
// }
let newNickname = '';
let newEmail = '';
let newPhone = '';

class PersonalInfo extends React.Component {
    constructor(props) {
        super(props);
        this.state={
            username:storage['user'],
            identity:storage['identity'],
            nickname:storage['nickname'],
            email:storage['email'],
            phone:storage['phone'],
            address:'',
            collapsed: false,
            infoModLoading:false,
            psdModLoading:false,
            oldPassword:'',
            newPassword:'',
            page:'1',
        }
    }



    componentDidMount() {
        newNickname = this.state.nickname
        newPhone = this.state.phone
        newEmail = this.state.email
    }

    onCollapse = collapsed => {
        console.log(collapsed);
        this.setState({
            collapsed: collapsed ,
        });
    };

    getNickname=(e)=>{
        newNickname = e.target.value
    }

    getEmail=(e)=>{
        newEmail = e.target.value
    }

    getPhone=(e)=>{
        newPhone = e.target.value
    }

    infoModPost=()=>{
        this.setState({
            infoModLoading:true
        });
        let params={
            uid:this.state.username,
            email:newEmail,
            nickname:newNickname,
            phone:newPhone,
        };
        console.log(params)
        axios({
            baseURL:defaultUrl,
            method:'post',
            url:'/infoModPage',
            params,
        }).then(res=>{
            console.log(res.data.msg)
            if(!res.data.code) {
                message.success('个人信息修改成功!');
                this.setState({
                    infoModLoading:false,
                    nickname:newNickname,
                    email:newEmail,
                    phone:newPhone,
                });
                // window.location.href = "http://localhost:3000"
            }
            else{
                message.error('修改失败请重试！');
                this.setState({
                    infoModLoading:false,
                });
            }
        })
    }

    getOldPassword=(e)=>{
        this.setState({
            oldPassword:e.target.value
        })
    }

    getNewPassword=(e)=>{
        this.setState({
            newPassword:e.target.value
        })
    }

    psdModPost=()=>{
        this.setState({
            psdModLoading:true
        });
        let params={
            uid:this.state.uid,
            oldPassword: this.state.oldPassword,
            newPassword: this.state.newPassword,
        };
        console.log(params)
        axios({
            baseURL:defaultUrl,
            method:'post',
            url:'/psdModPage',
            params,
        }).then(res=>{
            console.log(res.data.msg)
            if(!res.data.code) {
                message.success('密码修改成功，请重新登录!');
                this.setState({
                    psdModLoading:false,
                });
                storage.removeItem("user");
                storage.removeItem("token");
                storage.removeItem('identity')
                storage.removeItem("nickname");
                storage.removeItem("email");
                storage.removeItem('phone');
                storage.removeItem('address');
                window.location.href = "http://localhost:3000"
            }
            else{
                message.error('修改失败请重试！');
                this.setState({
                    psdModLoading:false,
                });
            }
        })
    }

    switchToPage1=()=>{
        this.setState({
            page:'1',
        })
    }

    switchToPage2=()=>{
        this.setState({
            page:'2',
        })
    }

    switchToPage3=()=>{
        this.setState({
            page:'3',
        })
    }

    commonUser = (
        <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
            <Menu.Item key="1" icon={<UserOutlined />} onClick={this.switchToPage1}>
                个人中心
            </Menu.Item>
            <Menu.Item key="2" icon={<DesktopOutlined />} onClick={this.switchToPage2}>
                预约记录
            </Menu.Item>
            <Menu.Item key="3" icon={<TeamOutlined />} onClick={this.switchToPage3}>
                咨询记录
            </Menu.Item>
        </Menu>
    )

    doctorUser = (
        <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
            <Menu.Item key="1" icon={<UserOutlined />}>
                个人中心
            </Menu.Item>
            <Menu.Item key="2" icon={<DesktopOutlined />}>
                预约记录
            </Menu.Item>
            <Menu.Item key="3" icon={<TeamOutlined />}>
                咨询记录
            </Menu.Item>
        </Menu>
    )


    userAppointment = (
        <Form>

        </Form>
    )

    userCounsel = (
        <Form>

        </Form>
    )


    render() {
        return (
            <Layout style={{ minHeight: '100vh' }}>
                <Sider collapsible collapsed={this.state.collapsed} onCollapse={this.onCollapse}>
                    <div className="logo"><Link to="/">返回</Link></div>
                    {
                        (this.state.identity==='0')?(
                            this.commonUser
                        ):(
                            this.doctorUser
                        )
                    }
                </Sider>

                <Layout className="site-layout">
                    <Header className="site-layout-background" style={{ padding: 0 }} />
                    <Content style={{ margin: '0 16px' }}>
                        <Breadcrumb style={{ margin: '16px 0' }}>
                            <Breadcrumb.Item>用户名</Breadcrumb.Item>
                            <Breadcrumb.Item>{this.state.nickname}</Breadcrumb.Item>
                        </Breadcrumb>
                        <div className="site-layout-background" style={{ padding: 24, minHeight: 360 }}>
                            {
                                (this.state.page==='1')?(
                                    <UserInfo></UserInfo>
                                ):(
                                    ''
                                )
                            }
                            {
                                (this.state.page==='2')?(
                                    this.userAppointment
                                ):(
                                    ''
                                )
                            }
                            {
                                (this.state.page==='3')?(
                                    this.userCounsel
                                ):(
                                    ''
                                )
                            }
                        </div>
                    </Content>
                    <Footer style={{ textAlign: 'center' }}>SE Design ©2021 Created by Online Medical System</Footer>
                </Layout>
            </Layout>
        );
    }
}

export default PersonalInfo;
