import React from 'react';
import {Layout, Menu, Breadcrumb, Form, Input, Cascader, Checkbox, Button, message} from 'antd';
import {
  DesktopOutlined,
  PieChartOutlined,
  FileOutlined,
  TeamOutlined,
  UserOutlined,
} from '@ant-design/icons';
import { Link } from 'react-router-dom';
import '../assets/css/PersonalInfo.css';
import BasicInformation from "../Part1File/BasicInformation";
import axios from "axios";

let storage = window.localStorage;
const defaultUrl = 'http://127.0.0.1:5003';

const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;
const residences = [
    {
        value: 'province',
        label: '省份',
        children: [
            {
                value: 'city',
                label: '城市',
                children: [
                  {
                    value: 'district',
                    label: '地区',
                  },
                ],
            },
        ],
    },
    {
        value: '浙江',
        label: '浙江',
        children: [
            {
                value: '杭州',
                label: '杭州',
                children: [
                    {
                        value: '上城区',
                        label: '上城区',
                    },
                    {
                        value: '西湖区',
                        label: '西湖区',
                    },
                    {
                        value: 'gongshu',
                        label: '拱墅区',
                    },
                    {
                        value: '江干区',
                        label: '江干区',
                    },
                    {
                        value: '临平区',
                        label: '临平区',
                    },
                    {
                        value: '余杭区',
                        label: '余杭区',
                    },
                    {
                        value: '萧山区',
                        label: '萧山区',
                    },
                    {
                        value: '滨江区',
                        label: '滨江区',
                    },
                    {
                        value: '钱塘区',
                        label: '钱塘区',
                    },
                    {
                        value: '富阳区',
                        label: '富阳区',
                    },
                    {
                        value: '临安区',
                        label: '临安区',
                    },
                ],
            },
        ],
    },

];

const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 8 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 16 },
  },
};
const tailFormItemLayout = {
  wrapperCol: {
    xs: {
      span: 24,
      offset: 0,
    },
    sm: {
      span: 16,
      offset: 8,
    },
  },
};

// let temp = storage['address']
// let userAddress = ['','','']
// for (let i = 0; i < temp.length; i++) {
//     let count = 0
//     if (temp[i] !== ',') {
//         userAddress[count] = userAddress[count] + temp[i]
//     } else {
//         count++;
//     }
// }


class PersonalInfo extends React.Component {
    constructor(props) {
        super(props);
        this.state={
            username:storage['user'],
            identity:storage['identity'],
            nickname:storage['nickname'],
            email:storage['email'],
            phone:storage['phone'],
            address:'',
            collapsed: false,
            infoModLoading:false,
            psdModLoading:false,
            oldPassword:'',
            newPassword:'',
            page:'1',
        }
    }

    componentDidMount() {


    }

    onCollapse = collapsed => {
        console.log(collapsed);
        this.setState({
            collapsed: collapsed ,
        });
    };

    getNickname=(e)=>{
        this.setState({
            nickname:e.target.value
        })
    }

    getEmail=(e)=>{
        this.setState({
            email:e.target.email
        })
    }

    getPhone=(e)=>{
        this.setState({
            phone:e.target.phone
        })
    }

    infoModPost=()=>{
        this.setState({
            infoModLoading:true
        });
        // let tmp = this.state.address[0]+this.state.address[1]+this.state.address[2]
        let params={
            uid:this.state.uid,
            email:this.state.email,
            nickname:this.state.nickname,
            // address:tmp,
            phone:this.state.phone,
        };
        console.log(params)
        axios({
            baseURL:defaultUrl,
            method:'post',
            url:'/infoModPage',
            params,
        }).then(res=>{
            console.log(res.data.msg)
            if(!res.data.code) {
                message.success('个人信息修改成功!');
                this.setState({
                    infoModLoading:false,
                });
                // window.location.href = "http://localhost:3000"
            }
            else{
                message.error('修改失败请重试！');
                this.setState({
                    infoModLoading:false,
                });
            }
        })
    }

    getOldPassword=(e)=>{
        this.setState({
            oldPassword:e.target.value
        })
    }

    getNewPassword=(e)=>{
        this.setState({
            newPassword:e.target.value
        })
    }

    psdModPost=()=>{
        this.setState({
            psdModLoading:true
        });
        let params={
            uid:this.state.uid,
            oldPassword: this.state.oldPassword,
            newPassword: this.state.newPassword,
        };
        console.log(params)
        axios({
            baseURL:defaultUrl,
            method:'post',
            url:'/psdModPage',
            params,
        }).then(res=>{
            console.log(res.data.msg)
            if(!res.data.code) {
                message.success('密码修改成功，请重新登录!');
                this.setState({
                    psdModLoading:false,
                });
                storage.removeItem("user");
                storage.removeItem("token");
                storage.removeItem('identity')
                storage.removeItem("nickname");
                storage.removeItem("email");
                storage.removeItem('phone');
                storage.removeItem('address');
                window.location.href = "http://localhost:3000"
            }
            else{
                message.error('修改失败请重试！');
                this.setState({
                    psdModLoading:false,
                });
            }
        })
    }

    switchToPage1=()=>{
        this.setState({
            page:'1',
        })
    }

    switchToPage2=()=>{
        this.setState({
            page:'2',
        })
    }

    switchToPage3=()=>{
        this.setState({
            page:'3',
        })
    }

    commonUser = (
        <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
            <Menu.Item key="1" icon={<UserOutlined />}>
                <div onClick={this.switchToPage1}>
                    个人中心
                </div>
            </Menu.Item>
            <Menu.Item key="2" icon={<DesktopOutlined />}>
                <div onClick={this.switchToPage2}>
                    预约记录
                </div>
            </Menu.Item>
            <Menu.Item key="3" icon={<TeamOutlined />}>
                <div onClick={this.switchToPage3}>
                    咨询记录
                </div>
            </Menu.Item>
        </Menu>
    )

    doctorUser = (
        <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
            <Menu.Item key="1" icon={<UserOutlined />}>
                个人中心
            </Menu.Item>
            <Menu.Item key="2" icon={<DesktopOutlined />}>
                预约记录
            </Menu.Item>
            <Menu.Item key="3" icon={<TeamOutlined />}>
                咨询记录
            </Menu.Item>
        </Menu>
    )

    userCenter = (
        <Form
            className="personalInfo-form"
            style={{width:"50%"}}
            {...formItemLayout}
            name="personalInfo"
            // initialValues={{
            //     residence: this.state.address,
            // }}
            scrollToFirstError
        >
            <Form.Item
                name="uid"
                label="身份证号"
            >
                {this.state.username}
            </Form.Item>

            <Form.Item
                name="nickname"
                label="用户昵称"

            >
                <Input
                    placeholder={this.state.nickname}
                    onChange={(e)=>this.getNickname(e)}
                />
            </Form.Item>

            <Form.Item
                name="email"
                label="注册邮箱"
                rules={[
                    {
                    type: 'email',
                    message: '请输入正确的邮件地址!',
                    }
                ]}
            >
                <Input
                    placeholder={this.state.email}
                    onChange={(e)=>this.getEmail(e)}
                />
            </Form.Item>

            <Form.Item
                name="phone"
                label="联系电话"
            >
                <Input
                    placeholder={this.state.phone}
                    onChange={(e)=>this.getPhone(e)}
                />
            </Form.Item>

            {/*<Form.Item*/}
            {/*    name="residence"*/}
            {/*    label="所在地"*/}
            {/*>*/}
            {/*    <Cascader*/}
            {/*        placeholder="Please select"*/}
            {/*        options={residences}*/}
            {/*        onChange={this.getAddress}*/}
            {/*    />*/}
            {/*</Form.Item>*/}

            <Form.Item {...tailFormItemLayout}>
                <Button
                    type="primary"
                    htmlType="submit"
                    onClick={this.infoModPost}
                    loading={this.state.infoModLoading}
                >
                    修改资料
                </Button>
            </Form.Item>



            <Form.Item
                name="oldPassword"
                label="输入旧密码"
                hasFeedback
            >
                <Input.Password onChange={(e)=>this.getOldPassword(e)}/>
            </Form.Item>

            <Form.Item
                name="newPassword"
                label="输入新密码"
                hasFeedback
            >
                <Input.Password/>
            </Form.Item>

            <Form.Item
                name="confirm"
                label="确认新密码"
                dependencies={['newPassword']}
                hasFeedback
                rules={[
                    ({ getFieldValue }) => ({validator(_, value) {
                        if (!value || getFieldValue('password') === value) {
                        return Promise.resolve();
                        }
                        return Promise.reject(new Error('两次密码不一致!'));
                    },}),
                ]}
            >
                <Input.Password onChange={(e)=>this.getNewPassword(e)}/>
            </Form.Item>

            <Form.Item {...tailFormItemLayout}>
                <Button
                    type="primary"
                    htmlType="submit"
                    onClick={this.psdModPost}
                    loading={this.state.psdModLoading}
                >
                    修改密码
                </Button>
            </Form.Item>
        </Form>
    )

    userAppointment = (
        <Form>

        </Form>
    )

    userCounsel = (
        <Form>

        </Form>
    )


    render() {
        return (
            <Layout style={{ minHeight: '100vh' }}>
                <Sider collapsible collapsed={this.state.collapsed} onCollapse={this.onCollapse}>
                    <div className="logo"><Link to="/">返回</Link></div>
                    {
                        (this.state.identity==='0')?(
                            this.commonUser
                        ):(
                            this.doctorUser
                        )
                    }
                </Sider>

                <Layout className="site-layout">
                    <Header className="site-layout-background" style={{ padding: 0 }} />
                    <Content style={{ margin: '0 16px' }}>
                        <Breadcrumb style={{ margin: '16px 0' }}>
                            <Breadcrumb.Item>用户名</Breadcrumb.Item>
                            <Breadcrumb.Item>{this.state.nickname}</Breadcrumb.Item>
                        </Breadcrumb>
                        <div className="site-layout-background" style={{ padding: 24, minHeight: 360 }}>
                            {
                                (this.state.page==='1')?(
                                    this.userCenter
                                ):(
                                    ''
                                )
                            }
                            {
                                (this.state.page==='2')?(
                                    this.userAppointment
                                ):(
                                    ''
                                )
                            }
                            {
                                (this.state.page==='3')?(
                                    this.userCounsel
                                ):(
                                    ''
                                )
                            }
                        </div>
                    </Content>
                    <Footer style={{ textAlign: 'center' }}>SE Design ©2021 Created by Online Medical System</Footer>
                </Layout>
            </Layout>
        );
    }
}

export default PersonalInfo;
