import React from 'react';
import { Link } from 'react-router-dom';
import '../assets/css/FindPasswordForm.css';
class FindPasswordForm extends React.Component {
    constructor() {
        super();
        this.state = {
            email: '',
        };
    }
    myChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
    }
    mySubmitHandler = (event) => {
        event.preventDefault();
        alert("Instructions sent!");
    }
    render() {
        return (
            <form onSubmit={this.mySubmitHandler}>
                <div class="find-password-form-container">
                    <label class="input-label">Email address</label>
                    <input class="input-information-email"
                           type='text'
                           name='email'
                           placeholder='Enter your email...'
                           onChange={this.myChangeHandler}
                    />
                    <input class="submit-button"
                           type='submit'
                           value='Send reset instructions'
                    />
                </div>
                <div className="back-to-login-container">
                    <a className="back-to-login"><Link to="/SignInPage/">Back To OM Login</Link></a>
                </div>
            </form>
        )
    }
}

export default FindPasswordForm;
