import React from 'react';
import {Layout, Menu, Breadcrumb, Form, Input, Cascader, Checkbox, Button} from 'antd';
import {
  DesktopOutlined,
  PieChartOutlined,
  FileOutlined,
  TeamOutlined,
  UserOutlined,
} from '@ant-design/icons';
import { Link } from 'react-router-dom';
import '../assets/css/PersonalInfo.css';
import BasicInformation from "../Part1File/BasicInformation";

let storage = window.localStorage;
const defaultUrl = 'http://127.0.0.1:5003';

const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;
const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 8 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 16 },
  },
};
const tailFormItemLayout = {
  wrapperCol: {
    xs: {
      span: 24,
      offset: 0,
    },
    sm: {
      span: 16,
      offset: 8,
    },
  },
};

class PersonalInfo extends React.Component {
    constructor(props) {
        super(props);
        this.state={
            username:storage['user'],
            identity:storage['identity'],
            collapsed: false,
        }
    }

    componentDidMount() {

    }

    onCollapse = collapsed => {
        console.log(collapsed);
        this.setState({
            collapsed: collapsed ,
        });
    };

    commonUser = (
        <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
            <Menu.Item key="1" icon={<UserOutlined />}>
                个人中心
            </Menu.Item>
            <Menu.Item key="2" icon={<DesktopOutlined />}>
                预约记录
            </Menu.Item>
            <Menu.Item key="3" icon={<TeamOutlined />}>
                咨询记录
            </Menu.Item>
        </Menu>
    )

    doctorUser = (
        <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
            <Menu.Item key="1" icon={<UserOutlined />}>
                个人中心
            </Menu.Item>
            <Menu.Item key="2" icon={<DesktopOutlined />}>
                预约记录
            </Menu.Item>
            <Menu.Item key="3" icon={<TeamOutlined />}>
                咨询记录
            </Menu.Item>
        </Menu>
    )

    render() {
        return (
            <Layout style={{ minHeight: '100vh' }}>
                <Sider collapsible collapsed={this.state.collapsed} onCollapse={this.onCollapse}>
                    <div className="logo"><Link to="/">返回</Link></div>
                    {
                        (this.state.identity==='0')?(
                            this.commonUser
                        ):(
                            this.doctorUser
                        )
                    }
                </Sider>

                <Layout className="site-layout">
                    <Header className="site-layout-background" style={{ padding: 0 }} />
                    <Content style={{ margin: '0 16px' }}>
                        <Breadcrumb style={{ margin: '16px 0' }}>
                            <Breadcrumb.Item>用户名</Breadcrumb.Item>
                            <Breadcrumb.Item>{this.state.username}</Breadcrumb.Item>
                        </Breadcrumb>
                        <div className="site-layout-background" style={{ padding: 24, minHeight: 360 }}>
                            <Form
                            className="personalInfo-form"
                            style={{width:"100%"}}
                            {...formItemLayout}
                            name="personalInfo"
                            scrollToFirstError
                            >
                            <Form.Item
                                name="uid"
                                label="身份证号"
                            >
                                {this.state.username}
                            </Form.Item>

                            <Form.Item
                                name="email"
                                label="邮箱"
                                rules={[
                                    {
                                    type: 'email',
                                    message: '请输入正确的邮件地址!',
                                    },
                                    {
                                    required: true,
                                    message: '请输入邮件地址!',
                                    },
                                ]}
                            >
                                <Input onChange={(e)=>this.getEmail(e)}/>
                            </Form.Item>

                            <Form.Item
                                name="password"
                                label="密码"
                                rules={[
                                    {
                                    required: true,
                                    message: '请输入密码!',
                                    },
                                ]}
                                hasFeedback
                            >
                                <Input.Password/>
                            </Form.Item>

                            <Form.Item
                                name="confirm"
                                label="确认密码"
                                dependencies={['password']}
                                hasFeedback
                                rules={[
                                    {
                                        required: true,
                                        message: '请在输入一遍密码!',
                                    },
                                    ({ getFieldValue }) => ({validator(_, value) {
                                        if (!value || getFieldValue('password') === value) {
                                        return Promise.resolve();
                                        }
                                        return Promise.reject(new Error('两次密码不一致!'));
                                    },}),
                                ]}
                            >
                                <Input.Password onChange={(e)=>this.getPassword(e)}/>
                            </Form.Item>

                            <Form.Item
                                name="nickname"
                                label="昵称"
                                tooltip="你希望别人怎么称呼你?"
                                rules={[{ required: true, message: '请输入昵称!', whitespace: true }]}
                            >
                                <Input onChange={(e)=>this.getNickname(e)}/>
                            </Form.Item>

                            <Form.Item
                                name="realName"
                                label="真实姓名"
                                rules={[{ required: true, message: '请输入真实姓名!', whitespace: true }]}
                            >
                                <Input onChange={(e)=>this.getRealName(e)}/>
                            </Form.Item>


                            <Form.Item
                                name="agreement"
                                valuePropName="checked"
                                rules={[
                                    {
                                    validator: (_, value) =>
                                      value ? Promise.resolve() : Promise.reject(new Error('请同意协议！')),
                                    },
                                ]}
                                {...tailFormItemLayout}
                            >
                                <Checkbox>
                                    我已阅读并同意 <a href="">《医疗预约平台用户注册协议》</a>
                                </Checkbox>
                            </Form.Item>

                            <Form.Item {...tailFormItemLayout}>
                                <Button
                                    type="primary"
                                    htmlType="submit"
                                    onClick={this.registerPost}
                                    loading={this.state.loading}
                                >
                                    注册
                                </Button>
                            </Form.Item>
                        </Form>
                            I'm a common user.
                        </div>
                    </Content>
                    <Footer style={{ textAlign: 'center' }}>SE Design ©2021 Created by Online Medical System</Footer>
                </Layout>
            </Layout>
        );
    }
}

export default PersonalInfo;
