import React, {Component , Fragment} from 'react';
import '../assets/css/PersonalInfo.css';
import {Link} from 'react-router-dom';
import { Menu } from 'antd';
import MenuItem from 'antd/lib/menu/MenuItem';

var storage1 = window.localStorage;//麻了，没搞懂

class PersonalInfo extends Component{
    constructor(props) {
        super(props);
        this.state = {
            current:storage1.getItem("TopBarKey"),
        }
    }

    handleClick = e => {
        this.setState({
            current:e.key,
        });
        storage1.setItem("TopBarKey",e.key);
    };
    render(){
        return(
            <Fragment>
            <div className = "PersonalInfoTitle"  >个人信息展示</div> 
            <div className="PersonalInfo">
                <Menu onClick={this.handleClick} selectedKeys={[this.state.current]} mode="horizontal">
                    <MenuItem key = "首页" >
                        <Link to = "/"> 预约记录 </Link>
                    </MenuItem>
                    <MenuItem key = "模块1" >
                        <Link to = "/"> 咨询记录 </Link>
                    </MenuItem>
                    <MenuItem key = "模块2" >
                        <Link to = "/"> 医生记录 </Link>
                    </MenuItem>
                    <MenuItem key = "模块3" >
                        <Link to = "/"> 医院记录 </Link>
                    </MenuItem>
                </Menu>
            </div>
            </Fragment>
        )
    }
}
export default PersonalInfo;
